A_StationsCall();
A_GenericDevicesCall();
A_MainFaults();

CASE State OF
	// machine not running
	0:
		ConveyorStation.Start := FALSE;
		ConveyorStation.Stop := FALSE;
		ConveyorStation.Hold := FALSE;

		RobotStation.Start := FALSE;
		RobotStation.Stop := FALSE;
		RobotStation.Hold := FALSE;

		IF StartButton AND NOT StopButton AND NOT HoldButton THEN
			ConveyorStation.Start := TRUE;
			RobotStation.Start := TRUE;
			State := 10;
		END_IF

	// wait for all station to be running	
	10:
		IF ConveyorStation.StationState = EnumStationState.Running AND RobotStation.StationState = EnumStationState.Running THEN
			ConveyorStation.Start := FALSE;
			RobotStation.Start := FALSE;
			State := 20;
		END_IF

	// running	
	20:
		;
	// hold state
	500:
		IF ConveyorStation.StationState = EnumStationState.Holding AND RobotStation.StationState = EnumStationState.Holding AND NOT HoldButton THEN
			ConveyorStation.Hold := FALSE;
			RobotStation.Hold := FALSE;
			State := 10;
		END_IF

	// stop state
	1000:
		IF ConveyorStation.StationState = EnumStationState.NotRunning AND RobotStation.StationState = EnumStationState.NotRunning THEN
			State := 0;
		END_IF

END_CASE


// emergency management
IF GlobalSignalList.AlarmsActive THEN
	State := 0;
END_IF


// stop management
IF StopButton AND State <> 0 AND State <> 1000 THEN
	ConveyorStation.Stop := TRUE;
	RobotStation.Stop := TRUE;
	State := 1000;
END_IF

// hold management
IF HoldButton AND State <> 0 AND State <> 1000 AND State <> 500 THEN
	ConveyorStation.Hold := TRUE;
	RobotStation.Hold := TRUE;
	State := 500;
END_IF

///*** Feedback Mapping ***///

CASE (FeedbackType) OF

	NoFeedback:
		InternalDisableFeedback := (DeviceState = Disabled) OR (DeviceState = Disabling AND Timeout);
		InternalEnableFeedback := (DeviceState = Enabled) OR (DeviceState = Enabling AND Timeout);

	SingleEnableFeedback:
		InternalDisableFeedback := (DeviceState = Disabled) OR (DeviceState = Disabling AND Timeout);
		InternalEnableFeedback := DeviceEnabledSensor;

	SingleDisableFeedback:
		InternalDisableFeedback := DeviceDisabledSensor;
		InternalEnableFeedback := (DeviceState = Enabled) OR (DeviceState = Enabling AND Timeout);

	DoubleFeedback:
		InternalDisableFeedback := DeviceDisabledSensor;
		InternalEnableFeedback := DeviceEnabledSensor;


END_CASE



///*** Generic Device State Machine ***///

CASE (DeviceState) OF

	Off:

		// Reset the timer
		InternalTimer.IN := FALSE;
		InternalTimer.PT := T#0S;

		IF EnableGenericDevice THEN
			IF DeviceCmd THEN
				DeviceState := Enabling;
			ELSE
				DeviceState := Disabling;
			END_IF
		END_IF


	Disabled:

		// Reset the timer
		InternalTimer.IN := FALSE;
		InternalTimer.PT := T#0S;

		// EnableRequest --> State = Enabling
		IF DeviceCmd THEN
			DeviceState := Enabling;
		END_IF


	Enabling:
		// Start the Timer with the ActivationTime
		InternalTimer.IN := TRUE;
		InternalTimer.PT := DeviceActivationTime;

		IF InternalEnableFeedback THEN
			DeviceState := Enabled;
		END_IF

		IF NOT DeviceCmd THEN
			// Reset the timer
			InternalTimer.IN := FALSE;
			InternalTimer.PT := T#0S;

			DeviceState := Disabling;
		END_IF


	Enabled: 
		// Reset the timer
		InternalTimer.IN := FALSE;
		InternalTimer.PT := T#0S;

		// DisableRequest --> State = Disabling
		IF NOT DeviceCmd THEN
			DeviceState := Disabling;
		END_IF


	Disabling:
		// Start the Timer with the DeactivationTime
		InternalTimer.IN := TRUE;
		InternalTimer.PT := DeviceDeactivationTime;

		IF InternalDisableFeedback THEN
			DeviceState := Disabled;
		END_IF

		IF DeviceCmd THEN
			// Reset the timer
			InternalTimer.IN := FALSE;
			InternalTimer.PT := T#0S;

			DeviceState := Enabling;
		END_IF

END_CASE

IF NOT EnableGenericDevice THEN
	DeviceState := Off;
END_IF

///*** Timer Call ***///
InternalTimer(IN:= , PT:= , Q=> Timeout, ET=> );


///*** Actuators Mapping ***///

CASE (ActuationType) OF

	SingleActuation:
		EnableDeviceActuator	:= (DeviceState = Enabling) OR (DeviceState = Enabled);

	DoubleActuation:
		EnableDeviceActuator 	:= (DeviceState = Enabling);
		DisableDeviceActuator	:= (DeviceState = Disabling);

END_CASE


///*** Fault Detection ***///


CASE (FeedBackType) OF

	NoFeedback:
		DeviceFault	:=	NoFault;


	SingleDisableFeedback:
		IF 	(DeviceState = Disabled AND NOT DeviceDisabledSensor) OR
			(DeviceState = Enabling AND DeviceDisabledSensor AND Timeout) OR
			(DeviceState = Enabled AND DeviceDisabledSensor) OR
			(DeviceState = Disabling AND NOT DeviceDisabledSensor AND Timeout) THEN

			DeviceFault := GenericFault;

		ELSE
			DeviceFault := NoFault;

		END_IF


	SingleEnableFeedback:
		IF 	(DeviceState = Disabled AND DeviceEnabledSensor) OR
			(DeviceState = Enabling AND NOT DeviceEnabledSensor AND Timeout) OR
			(DeviceState = Enabled AND NOT DeviceEnabledSensor) OR
			(DeviceState = Disabling AND DeviceEnabledSensor AND Timeout) THEN

			DeviceFault := GenericFault;

		ELSE
			DeviceFault := NoFault;
		END_IF



	DoubleFeedback:
		IF 		(DeviceState = Disabled AND NOT DeviceDisabledSensor AND NOT DeviceEnabledSensor) OR
				(DeviceState = Enabled AND DeviceDisabledSensor AND DeviceEnabledSensor) OR
				(DeviceState = Disabling AND NOT DeviceDisabledSensor AND NOT DeviceEnabledSensor AND Timeout) THEN

				DeviceFault := DisableFeedbackFault;

		ELSIF 	(DeviceState = Disabled AND DeviceDisabledSensor AND DeviceEnabledSensor) OR
				(DeviceState = Enabling AND NOT DeviceDisabledSensor AND NOT DeviceEnabledSensor AND Timeout) OR
				(DeviceState = Enabled AND NOT DeviceDisabledSensor AND NOT DeviceEnabledSensor) THEN

				DeviceFault := EnableFeedbackFault;

		ELSIF	(DeviceState = Disabled AND NOT DeviceDisabledSensor AND DeviceEnabledSensor) OR
				(DeviceState = Enabling AND DeviceDisabledSensor AND NOT DeviceEnabledSensor AND Timeout) OR
				(DeviceState = Enabled AND DeviceDisabledSensor AND NOT DeviceEnabledSensor) OR
				(DeviceState = Disabling AND NOT DeviceDisabledSensor AND DeviceEnabledSensor AND Timeout) THEN

				DeviceFault := ActuatorFault;

		ELSE

				DeviceFault := NoFault;
		END_IF



END_CASE

IF SignalId < 1 OR SignalId > MaximumNumberOfConfigurableSignals OR SignalType = None THEN
	RETURN;
END_IF


// New Signal
IF SignalActive AND NOT ActiveSignals[SignalId] AND SignalList.NumberOfActiveSignals < MaximumNumberOfSimultaneousSignals THEN

	// Activate Signal
	ActiveSignals[SignalId] := TRUE;

	// Update Number of Active Signals
	SignalList.NumberOfActiveSignals := SignalList.NumberOfActiveSignals + 1;

	CASE (SignalType) OF

		Alarm:
			SignalList.NumberOfActiveAlarms := SignalList.NumberOfActiveAlarms + 1;
			NewSignalIndex := SignalList.NumberOfActiveAlarms;

		Warning:
			SignalList.NumberOfActiveWarnings := SignalList.NumberOfActiveWarnings + 1;
			NewSignalIndex := SignalList.NumberOfActiveAlarms + SignalList.NumberOfActiveWarnings;

		Message:
			SignalList.NumberOfActiveMessages := SignalList.NumberOfActiveMessages + 1;
			NewSignalIndex := SignalList.NumberOfActiveSignals;

	END_CASE

	// Reorder List After new Signal
	FOR Index := SignalList.NumberOfActiveSignals TO NewSignalIndex + 1 BY -1 DO
		SignalList.ActiveSignals[Index] := SignalList.ActiveSignals[Index  - 1];
		SignalIndexesInList[SignalList.ActiveSignals[Index].SignalId] := Index;
	END_FOR

	// Add Signal To List in the Correct Index
	SignalList.ActiveSignals[NewSignalIndex].SignalName := SignalName;
	SignalList.ActiveSignals[NewSignalIndex].SignalId 	:= SignalId;
	SignalList.ActiveSignals[NewSignalIndex].SignalType := SignalType;
	SignalList.ActiveSignals[NewSignalIndex].Active 	:= TRUE;

	SignalIndexesinList[SignalId] := NewSignalIndex;
END_IF






// Reset Signal
IF NOT SignalActive AND ActiveSignals[SignalId] AND (Reset OR AutoReset) THEN

	// Reorder the List
	FOR Index := SignalIndexesinList[SignalId] TO SignalList.NumberOfActiveSignals - 1 DO
		SignalList.ActiveSignals[Index] := SignalList.ActiveSignals[Index + 1];
		SignalIndexesInList[SignalList.ActiveSignals[Index].SignalId] := Index;
	END_FOR

	// Update Number of Active Signals
	SignalList.NumberOfActiveSignals := SignalList.NumberOfActiveSignals - 1;

	CASE (SignalType) OF

		Alarm:
			SignalList.NumberOfActiveAlarms := SignalList.NumberOfActiveAlarms - 1;

		Warning:
			SignalList.NumberOfActiveWarnings := SignalList.NumberOfActiveWarnings - 1;

		Message:
			SignalList.NumberOfActiveMessages := SignalList.NumberOfActiveMessages - 1;

	END_CASE


	// Reset Last Signal in the List
	SignalList.ActiveSignals[SignalList.NumberOfActiveSignals + 1].SignalName := '';
	SignalList.ActiveSignals[SignalList.NumberOfActiveSignals + 1].SignalId := 0;
	SignalList.ActiveSignals[SignalList.NumberOfActiveSignals + 1].SignalType := None;
	SignalList.ActiveSignals[SignalList.NumberOfActiveSignals + 1].Active := FALSE;


	// Dectivate Signal
	ActiveSignals[SignalId] := FALSE;
	SignalIndexesinList[SignalId] := 0;
END_IF 




SignalList.AlarmsActive := SignalList.NumberOfActiveAlarms > 0;
SignalList.WarningsActive := SignalList.NumberOfActiveWarnings > 0;
SignalList.MessagesActive := SignalList.NumberOfActiveMessages > 0;

CASE (State) OF

	// Disabled
	0:
		IF Enable THEN
			State := 10;
			SignalOk := FALSE;
		END_IF

	// Monitoring
	10:
		IF SignalToMonitor THEN
			SignalOk := TRUE;
		END_IF


		IF NOT Enable THEN
			IF NOT SignalOk THEN
				Fault := TRUE;
			END_IF
			State := 0;
		END_IF
END_CASE


IF Reset THEN
	Fault := FALSE;
END_IF

CASE FillingState OF	
			// start state
			0:
				FillingDevice.DeviceCmd := TRUE;
				IF FillingDevice.DeviceState = Enabled THEN
					FillingState := 10;
					TimerFilling.IN := TRUE;
					TimerFilling.PT := FillingTime;
				END_IF

			// wait for filling	
			10:
				IF TimerFilling.Q THEN
					TimerFilling.IN := FALSE;
					FillingState := 20;
				END_IF

			// stop filling
			20:
				FillingDevice.DeviceCmd := FALSE;
				IF FillingDevice.DeviceState = Disabled THEN
					FillingState := 100;
				END_IF

			// finish state
			100:
				;
END_CASE

CASE CappingState OF
	// start state
	0:
		CappingDevice.DeviceCmd := TRUE;
		CappingMovementDevice.DeviceCmd := TRUE;

		IF CappingDevice.DeviceState = Enabled AND CappingMovementDevice.DeviceState = Enabled THEN
			CappingState := 10;
			TimerCapping.IN := TRUE;
			TimerCapping.PT := CappingTime;
		END_IF

	// wait capping time
	10:
		IF TimerCapping.Q THEN
			TimerCapping.IN := FALSE;
			CappingState := 20;
		END_IF

	// stop capping and move up	
	20:
		CappingDevice.DeviceCmd := FALSE;
		CappingMovementDevice.DeviceCmd := FALSE;

		IF CappingDevice.DeviceState = Disabled AND CappingMovementDevice.DeviceState = Disabled THEN
			CappingState := 100;
		END_IF

	// finish state
	100:
		;
END_CASE

rtrigProductOnStartConveyor(CLK:= ProductOnConveyorStartSensor );
rtrigProductOnFillingStation(CLK:= ProductOnFillingStationSensor );
rtrigProductOnCappingStation(CLK:= ProductOnCappingStationSensor );
rtrigProductOnPickupPosition(CLK:= ProductOnPickupPositionSensor );
ftrigProductOnPickupPosition(CLK:= ProductOnPickupPositionSensor );
TimerFilling();
TimerCapping();

TimerFaultFillingStationSensorDisabled(IN:=, PT:=TimeSensorFaultDisabled, Q:=, ET=>);
TimerFaultCappingStationSensorDisabled(IN:=, PT:=TimeSensorFaultDisabled, Q:=, ET=>);
TimerFaultPickupPositionSensorDisabled(IN:=, PT:=TimeSensorFaultDisabled, Q:=, ET=>);

TimerFaultFillingStationSensorEnabled(IN:=, PT:=TimeSensorFaultEnabled, Q:=, ET=>);
TimerFaultCappingStationSensorEnabled(IN:=, PT:=TimeSensorFaultEnabled, Q:=, ET=>);



// product count management
IF rtrigProductOnStartConveyor.Q THEN
	TimerFaultFillingStationSensorDisabled.IN := TRUE; 
	NumberOfProductsOnConveyor := NumberOfProductsOnConveyor +1; 
END_IF

IF rtrigProductOnFillingStation.Q THEN
	// reset 1st timer and set next
	// if all OK then the timer is reset before it elapses
	TimerFaultFillingStationSensorDisabled.IN := FALSE;
	TimerFaultCappingStationSensorDisabled.IN := TRUE;
END_IF

IF rtrigProductOnCappingStation.Q THEN
	// reset previous timer and set next
	// if all OK then the timer is reset before it elapses
	TimerFaultCappingStationSensorDisabled.IN := FALSE;
	TimerFaultPickupPositionSensorDisabled.IN := TRUE;
END_IF

IF rtrigProductOnPickupPosition.Q THEN
	TimerFaultPickupPositionSensorDisabled.IN := FALSE;
END_IF

// reset timer then no sensor detected
IF NOT ProductOnFillingStationSensor THEN
	TimerFaultFillingStationSensorEnabled.IN := FALSE;
END_IF

IF NOT ProductOnCappingStationSensor THEN
	TimerFaultCappingStationSensorEnabled.IN := FALSE;
END_IF


IF ftrigProductOnPickupPosition.Q THEN NumberOfProductsOnConveyor := NumberOfProductsOnConveyor -1; END_IF


CASE State OF

	// disabled
	0:
		TimerFilling.IN := FALSE;
		TimerCapping.IN := FALSE;

		TimerFaultFillingStationSensorDisabled.IN := FALSE;
		TimerFaultCappingStationSensorDisabled.IN := FALSE;
		TimerFaultPickupPositionSensorDisabled.IN := FALSE;

		TimerFaultFillingStationSensorEnabled.IN := FALSE;
		TimerFaultCappingStationSensorEnabled.IN := FALSE;

		ProductsInfeedEnable := FALSE;
		IF Start AND NOT Stop AND NOT Emergency AND NOT Hold THEN
			ConveyorDevice.EnableGenericDevice 			:= TRUE;
			FillingDevice.EnableGenericDevice 			:= TRUE;
			CappingMovementDevice.EnableGenericDevice 	:= TRUE;
			CappingDevice.EnableGenericDevice			:= TRUE;

			ConveyorDevice.DeviceCmd := FALSE;
			FillingDevice.DeviceCmd := FALSE;
			CappingDevice.DeviceCmd := FALSE;
			CappingMovementDevice.DeviceCmd := FALSE;

			State := 10;
		END_IF

	// 	wait for products
	10:
		ProductsInfeedEnable := TRUE;
		IF NumberOfProductsOnConveyor > 0 THEN
			State := 20;
		END_IF

	// start moving conveyor
	20: 
		ConveyorDevice.DeviceCmd := TRUE;
		IF ConveyorDevice.DeviceState = Enabled THEN

			IF ProductOnFillingStationSensor THEN
				TimerFaultFillingStationSensorEnabled.IN := TRUE;
			END_IF

			IF ProductOnCappingStationSensor THEN
				TimerFaultCappingStationSensorEnabled.IN := TRUE;
			END_IF

			State := 30;
		END_IF

	// wait for products below stations
	30:
		IF rtrigProductOnFillingStation.Q OR rtrigProductOnCappingStation.Q THEN
			IF 	rtrigProductOnFillingStation.Q THEN
				// start FSM for filling in step 50
				FillingState := 0;
			ELSE 
				FillingState := 100; 
			END_IF

			IF rtrigProductOnCappingStation.Q THEN
				// start FSM for capping in step 50
				CappingState := 0;
			ELSE
				CappingState := 100;
			END_IF

			ConveyorDevice.DeviceCmd := FALSE;
			State := 40;
		END_IF

	// wait for conveyor to stop
	40:
		IF ConveyorDevice.DeviceState = Disabled THEN
			State := 50;
		END_IF

	// processing state - multi nested FSM
	50:
		// ******* filling station state machine **********
		FillingStationStateMachine();

		// ****** capping station state machine ***********
		CappingStationStateMachine();

		IF FillingState = 100 AND CappingState = 100 THEN
			State := 10;
		END_IF

// ********************************************************

	// hold state
	500:

		TimerFaultFillingStationSensorDisabled.IN := FALSE;
	    TimerFaultCappingStationSensorDisabled.IN := FALSE;
		TimerFaultPickupPositionSensorDisabled.IN := FALSE;
		TimerFaultFillingStationSensorEnabled.IN := FALSE;
		TimerFaultCappingStationSensorEnabled.IN := FALSE;

		IF NOT Hold THEN
			State := HoldState;
			HoldState := 0;
		END_IF


	// stopping state	
	1000:
		ConveyorDevice.DeviceCmd := FALSE;
		FillingDevice.DeviceCmd := FALSE;
		CappingDevice.DeviceCmd := FALSE;
		CappingMovementDevice.DeviceCmd := FALSE;

		IF ConveyorDevice.DeviceState = Disabled AND FillingDevice.DeviceState = Disabled AND
			CappingDevice.DeviceState = Disabled AND CappingMovementDevice.DeviceState = Disabled THEN

			State := 0;
		END_IF

END_CASE


// emergency management
IF State <> 0 AND Emergency THEN
	State := 0;
	ConveyorDevice.EnableGenericDevice 			:= FALSE;
	FillingDevice.EnableGenericDevice 			:= False;
	CappingMovementDevice.EnableGenericDevice 	:= False;
	CappingDevice.EnableGenericDevice			:= False;
END_IF

// stop management
IF State <> 0 AND State <> 1000 AND Stop THEN
	State := 1000;
END_IF

// hold management
IF State <> 0 AND State <> 500 AND State <> 1000 AND Hold THEN
	HoldRequest := TRUE;
END_IF

IF State <> 500 AND State <> 20 AND State <> 30 AND State <> 50 AND HoldRequest THEN
	HoldRequest := FALSE;
	HoldState := State;
	State := 500;
END_IF

// ******* Station State management *********
IF (State = 0) THEN 	StationState := EnumStationState.NotRunning;
ELSIF State = 500 THEN 	StationState := EnumStationState.Holding;
ELSIF State = 1000 THEN StationState := EnumStationState.Stopping;
ELSE 					StationState := EnumStationState.Running;	
END_IF

// ******* Station Fault management *********
SignalManagement(
	SignalActive:= TimerFaultFillingStationSensorDisabled.Q , 
	SignalName:= 'Product On Filling Station Sensor Disabled Fault', 
	SignalId:= 30, 
	SignalType:= Alarm, 
	AutoReset:= FALSE, 
	Reset:= ResetButton, 
	SignalList:= GlobalSignalList);

SignalManagement(
	SignalActive:= TimerFaultCappingStationSensorDisabled.Q , 
	SignalName:= 'Product On Capping Station Sensor Disabled Fault', 
	SignalId:= 31, 
	SignalType:= Alarm, 
	AutoReset:= FALSE, 
	Reset:= ResetButton, 
	SignalList:= GlobalSignalList);

SignalManagement(
	SignalActive:= TimerFaultPickupPositionSensorDisabled.Q , 
	SignalName:= 'Product On Pickup Position Sensor Disabled Fault', 
	SignalId:= 32, 
	SignalType:= Alarm, 
	AutoReset:= FALSE, 
	Reset:= ResetButton, 
	SignalList:= GlobalSignalList);


SignalManagement(
	SignalActive:= TimerFaultFillingStationSensorEnabled.Q , 
	SignalName:= 'Product On Filling Station Sensor Enabled Fault', 
	SignalId:= 33, 
	SignalType:= Alarm, 
	AutoReset:= FALSE, 
	Reset:= ResetButton, 
	SignalList:= GlobalSignalList);

SignalManagement(
	SignalActive:= TimerFaultCappingStationSensorEnabled.Q , 
	SignalName:= 'Product On Capping Station Sensor Enabled Fault', 
	SignalId:= 34, 
	SignalType:= Alarm, 
	AutoReset:= FALSE, 
	Reset:= ResetButton, 
	SignalList:= GlobalSignalList);

CASE State OF

	// disabled
	0:
		IF Start AND NOT Stop AND NOT Hold AND NOT Emergency THEN
			RobotHorizontalDevice.EnableGenericDevice := TRUE;
			RobotVerticalDevice.EnableGenericDevice := TRUE;
			RobotGripperDevice.EnableGenericDevice := TRUE;
			State := 10;
		END_IF

	// move the robot to pickup position	
	10:
		RobotHorizontalDevice.DeviceCmd := FALSE;
		RobotVerticalDevice.DeviceCmd := FALSE;
		RobotGripperDevice.DeviceCmd := FALSE;
		IF  RobotHorizontalDevice.DeviceState = Disabled AND 
			RobotVerticalDevice.DeviceState = Disabled AND 
			RobotGripperDevice.DeviceState = Disabled THEN

			State := 20;
		END_IF

	// wait for product	
	20:		
		IF ProductOnPickupPositionSensor THEN
			State := 30;
		END_IF

	// grab product
	30:
		RobotGripperDevice.DeviceCmd := TRUE;
		IF RobotGripperDevice.DeviceState = Enabled THEN
			State := 40;
		END_IF

	// move product up
	40:
		PickupPositionSensorSignalMonitor.Enable := TRUE;
		PickupPositionSensorSignalMonitor.SignalToMonitor := NOT ProductOnPickupPositionSensor;

		RobotVerticalDevice.DeviceCmd := TRUE;
		IF RobotVerticalDevice.DeviceState = Enabled THEN
			PickupPositionSensorSignalMonitor.Enable := FALSE;
			State := 50;
		END_IF

	// move product right
	50:
		RobotHorizontalDevice.DeviceCmd := TRUE;
		IF RobotHorizontalDevice.DeviceState = Enabled THEN
			State := 60;
		END_IF

	// move product down
	60:	
		RobotVerticalDevice.DeviceCmd := FALSE;
		IF RobotVerticalDevice.DeviceState = Disabled THEN
			State := 70;
		END_IF	

	// release the product and go back
	70:
		RobotGripperDevice.DeviceCmd := FALSE;
		IF RobotGripperDevice.DeviceState = Disabled THEN
			State := 10;
		END_IF		

	// hold state
	500:
		IF NOT Hold THEN
			State := HoldState;
			HoldState := 0;
		END_IF

	// stopping state
	1000:
		RobotGripperDevice.DeviceCmd := FALSE;
		IF (RobotHorizontalDevice.DeviceState = Disabled OR RobotHorizontalDevice.DeviceState = Enabled) AND
		   (RobotVerticalDevice.DeviceState = Disabled OR RobotVerticalDevice.DeviceState = Enabled) AND
		   RobotGripperDevice.DeviceState = Disabled THEN

		   State :=0;

		END_IF

END_CASE


// emergency management
IF State <> 0 AND Emergency THEN
	State := 0;
	RobotHorizontalDevice.EnableGenericDevice := FALSE;
	RobotVerticalDevice.EnableGenericDevice := FALSE;
	RobotGripperDevice.EnableGenericDevice := FALSE;
END_IF

// stop management
IF State <> 0 AND State <> 1000 AND Stop THEN
	State := 1000;
END_IF

// hold management
IF State <> 0 AND State <> 500 AND State <> 1000 AND Hold THEN
	HoldRequest := TRUE;
END_IF

IF State <> 500 AND HoldRequest THEN
	HoldRequest := FALSE;
	HoldState := State;
	State := 500;
END_IF

// ******* StationState management *********
IF (State = 0) THEN 	StationState := EnumStationState.NotRunning;
ELSIF State = 500 THEN 	StationState := EnumStationState.Holding;
ELSIF State = 1000 THEN StationState := EnumStationState.Stopping;
ELSE 					StationState := EnumStationState.Running;	
END_IF

// ******* Station Fault management *********

PickupPositionSensorSignalMonitor(Enable:= , SignalToMonitor:= , Reset:=ResetButton , Fault=> );

SignalManagement(
	SignalActive:= PickupPositionSensorSignalMonitor.Fault , 
	SignalName:= 'Product On Pickup Position Sensor Fault OR Robot Gripper Fault', 
	SignalId:= 35, 
	SignalType:= Alarm, 
	AutoReset:= FALSE, 
	Reset:= ResetButton, 
	SignalList:= GlobalSignalList);
